#----------------------------------------------------------------------------------------------------------------#
# Pour mesurer la length des string

def longueur(mesure)
    puts mesure.length
end
#----------------------------------------------------------------------------------------------------------------#
# Choose the shortest in the list

def short(shorter)
    liluser = shorter.max_by { |x| -x.length }
    puts liluser
end
#----------------------------------------------------------------------------------------------------------------#
# Choose Only Five Letters

def five(fiver)

array = []

fiver.each do |item|
        
        if item.length == 6
            array.push(item)
        end
    end
    puts "here #{array}"
end
#----------------------------------------------------------------------------------------------------------------#
# Put in the correct alphabetical order


def alphabet(alpha)
    alpha.sort_by!{ |i| i.downcase }
    puts alpha
end
#----------------------------------------------------------------------------------------------------------------#
# How many handles starts with a capital letter

def uppercase(upper)

    puts upper.count{|handle| handle[1].match?( /[[:upper:]]/ )}

end
#----------------------------------------------------------------------------------------------------------------#
# Sorts the handle list by handle size (smallest first, largest after)
def sizer(size)

    size = size.sort_by(&:length)
    puts size
end
#----------------------------------------------------------------------------------------------------------------#
# What is the position in the array of the person @epenser
def position(find)
    puts find.index("@epenser")
end
